INSERT INTO `product_description` VALUES (406,'育婴米粉','&lt;p&gt;\r\n	&lt;strong&gt;配料&amp;nbsp;：&lt;/strong&gt;大米粉、番茄酱、菠菜粉、玉米油、碳酸钙、磷酸氢二钾、精炼金枪鱼油、富马酸亚铁、硫酸锌、维生素A、维生素D3，维生素E、维生素B1、维生素B2、烟酰胺，泛酸钙、维生素B6、叶酸、维生素B12、维生素C、生物素、双歧杆菌。可能含有微量的牛奶和谷蛋白 。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	置于阴凉干燥处\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;冲调指南：&lt;/strong&gt;&lt;br /&gt;\r\n1、清洁双手，取出已消毒的宝宝餐具备用。&lt;br /&gt;\r\n2、先将160ML的温奶倒入碗中。&lt;br /&gt;\r\n3、再在碗中倒入6平勺米粉，边倒边用勺搅拌。&lt;br /&gt;\r\n4、继续搅拌，直至混合均匀呈半流质糊状即可食用。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;请不要将未吃完的米糊留存。开袋后的配方米米请用密封夹封好，放置阴凉干燥处保存，开袋后请尽快食用完。&lt;/strong&gt;\r\n&lt;/p&gt;','','',NULL);
