INSERT INTO `product_description` VALUES (836,'贵州国宾酒 50° 1*6(450ml)  ','品 &amp;nbsp;名：贵州国宾酒 50°&lt;br /&gt;\r\n规 &amp;nbsp;格：1*6(450ml)&amp;nbsp;&lt;br /&gt;\r\n酒精度：50°&lt;br /&gt;\r\n香 &amp;nbsp;型：浓香型&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n“贵州国宾酒50°”在继承和保留了贵州茅台镇原有的酿酒工艺上，进行技术创新，丰富产品线。具有润而不燥，回味清爽的特点。&lt;br /&gt;\r\n&lt;img src=&quot;image/data/2013/04/11/192331_62075.png&quot; alt=&quot;&quot; /&gt;&lt;img src=&quot;image/data/2013/04/11/192332_92351.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;','','',NULL);
INSERT INTO `product_description` VALUES (837,'贵州国宾酒陈酿  1*6(500ml) 52°','品 &amp;nbsp;名：贵州国宾酒 陈酿&lt;br /&gt;\r\n规 &amp;nbsp;格：1*6(500ml)&amp;nbsp;&lt;br /&gt;\r\n酒精度：52°&lt;br /&gt;\r\n香 &amp;nbsp;型：浓香型&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	“贵州国宾酒 陈酿” 选用双轮窖池窖底原酒，结合古法勾兑工艺，入口绵爽，饮后醇香。系贵州茅山酒业在浓香型酒系列的战略产品。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;image/data/2013/04/11/192648_91413.png&quot; alt=&quot;&quot; /&gt;&lt;img src=&quot;image/data/2013/04/11/192649_61425.png&quot; alt=&quot;&quot; /&gt; \r\n&lt;/p&gt;','','',NULL);
