INSERT INTO `product_description` VALUES (840,'贵州国宾酒  普彩 1*6(450ml)  52°','品 &amp;nbsp;名：贵州国宾酒 普彩&lt;br /&gt;\r\n规 &amp;nbsp;格：1*6(450ml) /1*4(450ml)&lt;br /&gt;\r\n酒精度：52°/45°/38°&lt;br /&gt;\r\n香 &amp;nbsp;型：浓香型&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n“贵州国宾酒普彩”在继承和保留了贵州茅台镇原有的酿酒工艺上，进行技术创新。具有口感绵爽，润而不燥的特点。适合各个地区的口味。（注：52°38°规格1*6,45°规格1*4)&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;image/data/2013/04/11/200835_52094.png&quot; alt=&quot;&quot; /&gt;&lt;img src=&quot;image/data/2013/04/11/200837_17616.png&quot; alt=&quot;&quot; /&gt; \r\n&lt;/p&gt;','','',NULL);
INSERT INTO `product_description` VALUES (841,'贵州国宾酒十五年份酒  450m  l*6  52°','品 &amp;nbsp; 名：贵州国宾酒十五年份酒&lt;br /&gt;\r\n规 &amp;nbsp; 格：450ml*6&lt;br /&gt;\r\n酒精度 ：52°&lt;br /&gt;\r\n香 &amp;nbsp; 型：浓香型&lt;br /&gt;\r\n&amp;nbsp;&lt;br /&gt;\r\n&lt;p&gt;\r\n	&amp;nbsp; &amp;nbsp; “贵州国宾酒十五年年份酒”，充分继承了茅台镇酿酒工艺的精华。采用窖藏十五年以上的原酒基酒，由茅台镇著名调酒师精心调制而成，是贵州国宾酒系列酒的扛鼎之作。此酒兼有酱香型白酒和浓香型白酒特有的风格。具有入口醇香，回味悠长，润而不燥，醉不伤身的特点。特别适合高端商务用酒，企事业单位招待用酒。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;image/data/2013/04/11/203858_15191.png&quot; alt=&quot;&quot; /&gt;&lt;img src=&quot;image/data/2013/04/11/203907_48033.png&quot; alt=&quot;&quot; /&gt; \r\n&lt;/p&gt;','','',NULL);
INSERT INTO `product_description` VALUES (842,'贵州国宾酒  双凤  1*6(450ml)  52°','品 &amp;nbsp;名：贵州国宾酒 双凤&lt;br /&gt;\r\n规 &amp;nbsp;格：1*6(450ml)&amp;nbsp;&lt;br /&gt;\r\n酒精度：52°&lt;br /&gt;\r\n香 &amp;nbsp;型：浓香型&lt;br /&gt;\r\n&lt;br /&gt;\r\n&lt;br /&gt;\r\n贵州国宾酒 双凤，系贵州国宾酒系列的高端产品，采用双凤为主格调，凸显了“双凤呈祥”的美好寓意，特别适合婚宴，商务招待使用。&lt;br /&gt;\r\n&lt;img src=&quot;image/data/2013/04/12/094442_84914.png&quot; alt=&quot;&quot; /&gt;&lt;img src=&quot;image/data/2013/04/12/094442_69879.png&quot; alt=&quot;&quot; /&gt;&lt;br /&gt;','','',NULL);
